shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, boolean, index, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table for Replit Auth
// (IMPORTANT) This table is mandatory for Replit Auth, don't drop it.
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table for Replit Auth + Stripe
// (IMPORTANT) This table is mandatory for Replit Auth, don't drop it.
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique().notNull(),
  password: varchar("password").notNull(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),

  // Subscription fields
  stripeCustomerId: varchar("stripe_customer_id"),
  stripeSubscriptionId: varchar("stripe_subscription_id"),
  subscriptionStatus: varchar("subscription_status"), // trialing, active, canceled, past_due
  trialStartedAt: timestamp("trial_started_at"),
  trialEndsAt: timestamp("trial_ends_at"),
  activePropertyCount: varchar("active_property_count").default("0"),

  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const properties = pgTable("properties", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id, { onDelete: "cascade" }),
  accessKey: varchar("access_key").notNull().unique().default(sql`substring(md5(random()::text), 1, 12)`),
  name: text("name").notNull(),
  description: text("description").notNull(),

  // Localisation
  address: text("address").notNull(),
  floor: text("floor"),
  doorCode: text("door_code"),
  accessInstructions: text("access_instructions"),

  // Check-in / Check-out
  checkInTime: text("check_in_time").notNull().default("15:00"),
  checkOutTime: text("check_out_time").notNull().default("11:00"),
  checkInProcedure: text("check_in_procedure"),
  checkOutProcedure: text("check_out_procedure"),
  keyLocation: text("key_location"),

  // WiFi et connectivité
  wifiName: text("wifi_name"),
  wifiPassword: text("wifi_password"),

  // Équipements
  amenities: text("amenities").array().notNull().default(sql`ARRAY[]::text[]`),
  kitchenEquipment: text("kitchen_equipment"),

  // Règles de la maison
  houseRules: text("house_rules").notNull().default(""),
  maxGuests: text("max_guests"),
  petsAllowed: boolean("pets_allowed").default(false),
  smokingAllowed: boolean("smoking_allowed").default(false),
  partiesAllowed: boolean("parties_allowed").default(false),

  // Parking et transports
  parkingInfo: text("parking_info"),
  publicTransport: text("public_transport"),

  // Commerces et services
  nearbyShops: text("nearby_shops"),
  restaurants: text("restaurants"),

  // Urgences et contacts
  hostName: text("host_name").notNull(),
  hostPhone: text("host_phone"),
  emergencyContact: text("emergency_contact"),

  // Instructions spéciales
  heatingInstructions: text("heating_instructions"),
  garbageInstructions: text("garbage_instructions"),
  applianceInstructions: text("appliance_instructions"),

  // Info additionnelle
  additionalInfo: text("additional_info"),
  faqs: text("faqs"),

  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const conversations = pgTable("conversations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  propertyId: varchar("property_id").notNull().references(() => properties.id, { onDelete: "cascade" }),
  guestName: text("guest_name").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  lastMessageAt: timestamp("last_message_at").defaultNow().notNull(),
});

export const messages = pgTable("messages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  conversationId: varchar("conversation_id").notNull().references(() => conversations.id, { onDelete: "cascade" }),
  content: text("content").notNull(),
  isBot: boolean("is_bot").notNull().default(false),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  trialStartedAt: true,
  trialEndsAt: true,
  subscriptionStatus: true,
  stripeCustomerId: true,
  stripeSubscriptionId: true,
  activePropertyCount: true,
  profileImageUrl: true,
});

export const registerSchema = insertUserSchema.extend({
  email: z.string().email("Email invalide"),
  password: z.string().min(6, "Le mot de passe doit contenir au moins 6 caractères"),
});

export const loginSchema = z.object({
  email: z.string().email("Email invalide"),
  password: z.string().min(1, "Mot de passe requis"),
});

export const upsertUserSchema = createInsertSchema(users).omit({
  createdAt: true,
  updatedAt: true,
  trialStartedAt: true,
  trialEndsAt: true,
  subscriptionStatus: true,
  stripeCustomerId: true,
  stripeSubscriptionId: true,
  activePropertyCount: true,
});

export const insertPropertySchema = createInsertSchema(properties).omit({
  id: true,
  userId: true,
  accessKey: true,
  createdAt: true,
});

export const insertConversationSchema = createInsertSchema(conversations).omit({
  id: true,
  createdAt: true,
  lastMessageAt: true,
});

export const insertMessageSchema = createInsertSchema(messages).omit({
  id: true,
  createdAt: true,
});

export type InsertProperty = z.infer<typeof insertPropertySchema>;
export type Property = typeof properties.$inferSelect;

export type InsertConversation = z.infer<typeof insertConversationSchema>;
export type Conversation = typeof conversations.$inferSelect;

export type InsertMessage = z.infer<typeof insertMessageSchema>;
export type Message = typeof messages.$inferSelect;

export type InsertUser = z.infer<typeof insertUserSchema>;
export type RegisterUser = z.infer<typeof registerSchema>;
export type LoginUser = z.infer<typeof loginSchema>;
export type UpsertUser = z.infer<typeof upsertUserSchema>;
export type User = typeof users.$inferSelect;

client/src/pages/Pricing.tsx.content:
import { Button } from "@/components/ui/button";
import { Card, CardHeader, CardTitle, CardDescription, CardContent } from "@/components/ui/card";
import { Check, Sparkles } from "lucide-react";
import { useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useLanguage } from "@/contexts/LanguageContext";
import { motion } from "framer-motion";

export default function Pricing() {
  const [, setLocation] = useLocation();
  const { isAuthenticated } = useAuth();
  const { t, language } = useLanguage();

  const handleStartTrial = () => {
    if (isAuthenticated) {
      setLocation("/host");
    } else {
      window.location.href = "/api/login";
    }
  };

  const features = [
    t.pricingPage.features.unlimitedProperties,
    t.pricingPage.features.unlimitedConversations,
    t.pricingPage.features.aiAssistant,
    t.pricingPage.features.emailSupport,
    t.pricingPage.features.fullManagement,
    t.pricingPage.features.uniqueLinks,
    t.pricingPage.features.modernInterface,
  ];

  // Calculate example prices
  const firstPropertyPrice = 19.90;
  const additionalPropertyPrice = 14.90;
  const calculatePrice = (numProperties: number) => {
    if (numProperties === 0) return 0;
    return firstPropertyPrice + (additionalPropertyPrice * (numProperties - 1));
  };

  // Format price according to current language
  const formatPrice = (amount: number) => {
    // Use French format (19,90€) or English format (€19.90) based on language
    const formatted = language === 'fr' || language === 'es' || language === 'it'
      ? `${amount.toFixed(2).replace('.', ',')}€`
      : `€${amount.toFixed(2)}`;
    return formatted;
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-16">
        <motion.div 
          className="text-center mb-12"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <h1 className="text-4xl font-bold mb-4">{t.pricingPage.title}</h1>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            {t.pricingPage.subtitle}
          </p>
        </motion.div>

        <motion.div 
          className="max-w-2xl mx-auto"
          initial={{ opacity: 0, scale: 0.95, y: 30 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <Card className="border-primary shadow-lg relative hover-elevate">
            <motion.div 
              className="absolute -top-4 left-1/2 -translate-x-1/2"
              initial={{ y: -20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4, duration: 0.5 }}
            >
              <span className="bg-primary text-primary-foreground px-4 py-1 rounded-full text-sm font-medium flex items-center gap-1 shadow-lg">
                <motion.div
                  animate={{ rotate: [0, 10, -10, 0] }}
                  transition={{ duration: 2, repeat: Infinity }}
                >
                  <Sparkles className="w-4 h-4" />
                </motion.div>
                {t.pricingPage.launchOffer}
              </span>
            </motion.div>

            <CardHeader className="text-center pb-8">
              <div className="space-y-4">
                <div>
                  <CardTitle className="text-2xl">{t.pricingPage.firstProperty}</CardTitle>
                  <CardDescription className="text-3xl font-bold text-foreground mt-2">
                    {formatPrice(firstPropertyPrice)} <span className="text-base font-normal text-muted-foreground">{t.pricingPage.perMonth}</span>
                  </CardDescription>
                </div>
                <div className="text-sm text-muted-foreground">+</div>
                <div>
                  <CardTitle className="text-xl">{t.pricingPage.additionalProperty}</CardTitle>
                  <CardDescription className="text-2xl font-bold text-foreground mt-2">
                    {formatPrice(additionalPropertyPrice)} <span className="text-base font-normal text-muted-foreground">{t.pricingPage.perMonth}</span>
                  </CardDescription>
                </div>
              </div>

              <div className="mt-6 p-4 bg-primary/10 rounded-lg">
                <p className="text-sm font-medium">{t.pricingPage.trialBanner}</p>
                <p className="text-xs text-muted-foreground mt-1">
                  {t.pricingPage.trialDescription}
                </p>
              </div>
            </CardHeader>

            <CardContent className="space-y-6">
              <div>
                <h3 className="font-semibold mb-4">{t.pricingPage.allFeatures}</h3>
                <ul className="space-y-3">
                  {features.map((feature, idx) => (
                    <motion.li 
                      key={idx} 
                      className="flex items-start gap-2"
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.6 + idx * 0.1, duration: 0.4 }}
                    >
                      <motion.div
                        initial={{ scale: 0 }}
                        animate={{ scale: 1 }}
                        transition={{ delay: 0.7 + idx * 0.1, type: "spring" }}
                      >
                        <Check className="w-5 h-5 text-primary flex-shrink-0 mt-0.5" />
                      </motion.div>
                      <span>{feature}</span>
                    </motion.li>
                  ))}
                </ul>
              </div>

              <div className="pt-4 border-t">
                <h4 className="font-semibold mb-2">{t.pricingPage.howItWorks.title}</h4>
                <div className="space-y-2 text-sm text-muted-foreground">
                  <div className="flex items-start gap-2">
                    <Check className="w-4 h-4 text-primary flex-shrink-0 mt-0.5" />
                    <span>{t.pricingPage.howItWorks.step1}</span>
                  </div>
                  <div className="flex items-start gap-2">
                    <Check className="w-4 h-4 text-primary flex-shrink-0 mt-0.5" />
                    <span>{t.pricingPage.howItWorks.step2}</span>
                  </div>
                  <div className="flex items-start gap-2">
                    <Check className="w-4 h-4 text-primary flex-shrink-0 mt-0.5" />
                    <span>{t.pricingPage.howItWorks.step3firstPart} <strong>{t.pricingPage.howItWorks.step3price}</strong>, {t.pricingPage.howItWorks.step3additional}</span>
                  </div>
                  <div className="flex items-start gap-2">
                    <Check className="w-4 h-4 text-primary flex-shrink-0 mt-0.5" />
                    <span>{t.pricingPage.howItWorks.step4}</span>
                  </div>
                </div>
              </div>

              <Button 
                className="w-full" 
                size="lg"
                onClick={handleStartTrial}
                data-testid="button-start-trial"
              >
                {t.pricingPage.cta}
              </Button>

              <div className="text-center text-sm text-muted-foreground space-y-1">
                <p>{t.pricingPage.examplePrefix} 1 {t.pricingPage.exampleProperty} = {formatPrice(calculatePrice(1))}{t.pricingPage.exampleSuffix}</p>
                <p>{t.pricingPage.examplePrefix} 2 {t.pricingPage.exampleProperties} = {formatPrice(calculatePrice(2))}{t.pricingPage.exampleSuffix}</p>
                <p>{t.pricingPage.examplePrefix} 3 {t.pricingPage.exampleProperties} = {formatPrice(calculatePrice(3))}{t.pricingPage.exampleSuffix}</p>
              </div>
            </CardContent>
          </Card>
        </motion.div>

        <motion.div 
          className="text-center mt-12 space-y-2 text-sm text-muted-foreground"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1.5, duration: 0.6 }}
        >
          <p>{t.pricingPage.paymentMethods}</p>
          <p>{t.pricingPage.noCommitment}</p>
        </motion.div>
      </div>
    </div>
  );
}

