@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;
  --foreground: 0 0% 9%;
  --border: 0 0% 89%;
  --card: 0 0% 98%;
  --card-foreground: 0 0% 9%;
  --card-border: 0 0% 92%;
  --sidebar: 0 0% 96%;
  --sidebar-foreground: 0 0% 9%;
  --sidebar-border: 0 0% 90%;
  --sidebar-primary: 342 82% 48%;
  --sidebar-primary-foreground: 0 0% 98%;
  --sidebar-accent: 342 8% 92%;
  --sidebar-accent-foreground: 0 0% 9%;
  --sidebar-ring: 342 82% 48%;
  --popover: 0 0% 94%;
  --popover-foreground: 0 0% 9%;
  --popover-border: 0 0% 88%;
  --primary: 342 82% 48%;
  --primary-foreground: 0 0% 98%;
  --secondary: 0 0% 91%;
  --secondary-foreground: 0 0% 9%;
  --muted: 342 6% 93%;
  --muted-foreground: 0 0% 45%;
  --accent: 342 10% 94%;
  --accent-foreground: 0 0% 9%;
  --destructive: 0 84% 42%;
  --destructive-foreground: 0 0% 98%;
  --input: 0 0% 70%;
  --ring: 342 82% 48%;
  --chart-1: 342 82% 48%;
  --chart-2: 24 94% 50%;
  --chart-3: 142 71% 45%;
  --chart-4: 221 83% 53%;
  --chart-5: 280 67% 55%;

  --font-sans: Circular, Inter, system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .75rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.1);
  --shadow-sm: 0px 2px 4px -1px hsl(0 0% 0% / 0.06), 0px 1px 2px -1px hsl(0 0% 0% / 0.1);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.1), 0px 2px 4px -2px hsl(0 0% 0% / 0.1);
  --shadow-md: 0px 6px 12px -2px hsl(0 0% 0% / 0.12), 0px 3px 6px -3px hsl(0 0% 0% / 0.08);
  --shadow-lg: 0px 10px 20px -3px hsl(0 0% 0% / 0.1), 0px 4px 8px -4px hsl(0 0% 0% / 0.08);
  --shadow-xl: 0px 20px 30px -5px hsl(0 0% 0% / 0.12), 0px 8px 12px -6px hsl(0 0% 0% / 0.08);
  --shadow-2xl: 0px 30px 50px -8px hsl(0 0% 0% / 0.15);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

