import { storage } from "./storage";
import { scrypt, randomBytes } from "crypto";
import { promisify } from "util";

const scryptAsync = promisify(scrypt);

async function hashPassword(password: string) {
  const salt = randomBytes(16).toString("hex");
  const buf = (await scryptAsync(password, salt, 64)) as Buffer;
  return `${buf.toString("hex")}.${salt}`;
}

async function createAdminUser() {
  const email = "nguilane.fall@gmail.com";
  const password = "Boss4922";
  const firstName = "Admin";
  const lastName = "User";

  try {
    // Check if user already exists
    const existingUser = await storage.getUserByEmail(email);
    if (existingUser) {
      console.log("User already exists, updating subscription status...");

      // Set trial to far future (10 years)
      const trialStart = new Date();
      const trialEnd = new Date();
      trialEnd.setFullYear(trialEnd.getFullYear() + 10);

      await storage.updateUserSubscription(existingUser.id, "active");
      await storage.updateUserTrial(existingUser.id, trialStart, trialEnd);

      console.log("✅ User subscription updated successfully!");
      console.log(`Email: ${email}`);
      console.log(`Status: Active (trial until ${trialEnd.toISOString()})`);
      return;
    }

    // Hash password
    const hashedPassword = await hashPassword(password);

    // Create user with active subscription
    const trialStart = new Date();
    const trialEnd = new Date();
    trialEnd.setFullYear(trialEnd.getFullYear() + 10); // 10 years trial

    const user = await storage.createUser({
      email,
      password: hashedPassword,
      firstName,
      lastName,
    });

    // Update subscription status
    await storage.updateUserSubscription(user.id, "active");
    await storage.updateUserTrial(user.id, trialStart, trialEnd);

    console.log("✅ Admin user created successfully!");
    console.log(`Email: ${email}`);
    console.log(`Password: ${password}`);
    console.log(`Status: Active (trial until ${trialEnd.toISOString()})`);
  } catch (error) {
    console.error("❌ Error creating admin user:", error);
    throw error;
  }
}

createAdminUser()
  .then(() => process.exit(0))
  .catch((err) => {
    console.error(err);
    process.exit(1);
  });
